"use client"

import { useState } from "react"
import { loadStripe } from "@stripe/stripe-js"
import { api } from "../services/api"

const StripeCheckout = ({ onPurchase, isIssued, price = 1, userId }) => {
  const [isLoading, setIsLoading] = useState(false)

  const handleStripeCheckout = async () => {
    setIsLoading(true)

    try {
      // Check if Stripe is configured
      const stripeKey = process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY
      
      if (!stripeKey || stripeKey === 'pk_test_your_stripe_publishable_key_here') {
        // Demo mode - simulate checkout process
        console.log('Demo mode: Simulating Stripe checkout...')
        
        // Simulate API call
        const session = await api.createCheckoutSession(price * 100, userId, isIssued)

        // Simulate successful payment
        setTimeout(() => {
          onPurchase()
          setIsLoading(false)
          alert('Demo: Payment successful! You have been charged €' + price)
        }, 2000)

        return
      }

      // Real Stripe integration
      const stripe = await loadStripe(stripeKey)

      // Create checkout session
      const session = await api.createCheckoutSession(price * 100, userId, isIssued)

      // Redirect to Stripe Checkout
      const result = await stripe.redirectToCheckout({
        sessionId: session.id,
      })

      if (result.error) {
        console.error('Error redirecting to checkout:', result.error)
      }
    } catch (error) {
      console.error('Error during checkout:', error)
      alert('Error during checkout. Please try again.')
    } finally {
      setIsLoading(false)
    }
  }

  return (
    <button
      onClick={handleStripeCheckout}
      disabled={isLoading}
      className={`w-full font-body font-semibold py-3 px-6 rounded-lg transition-all duration-200 hover:shadow-lg transform hover:scale-105 ${
        isLoading ? "bg-gray-400 cursor-not-allowed" : "bg-primary hover:bg-primary/90 text-white"
      }`}
    >
      {isLoading ? (
        <div className="flex items-center justify-center">
          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
          Processing...
        </div>
      ) : (
        `${isIssued ? "Create Now" : "Buy Now"} - €${price}`
      )}
    </button>
  )
}

export default StripeCheckout
