"use client"

import { useState, useEffect } from "react"
import Header from "./components/Header"
import Footer from "./components/Footer"
import HeroSection from "./components/HeroSection"
import CollectiblesGrid from "./components/CollectiblesGrid"
import PurchasedCards from "./components/PurchasedCards"
import RaffleWidget from "./components/RaffleWidget"

const EvaltreeExclusive = () => {
  const [userId, setUserId] = useState(null)
  const [issuedCards, setIssuedCards] = useState(new Set())
  const [activeTab, setActiveTab] = useState('marketplace') // 'marketplace' or 'collection'

  // Generate or retrieve user ID on component mount
  useEffect(() => {
    const storedUserId = localStorage.getItem('avaltree_user_id')
    if (storedUserId) {
      setUserId(storedUserId)
    } else {
      const newUserId = `user_${Math.random().toString(36).substr(2, 9)}`
      localStorage.setItem('avaltree_user_id', newUserId)
      setUserId(newUserId)
    }
  }, [])

  const collectibles = [
    {
      id: 1,
      title: "Digital Masterpiece #001",
      available: 50,
      price: 1,
      image: "/placeholder.svg?height=300&width=300",
    },
    {
      id: 2,
      title: "Cyber Genesis Collection",
      available: 25,
      price: 1.5,
      image: "/placeholder.svg?height=300&width=300",
    },
    {
      id: 3,
      title: "Abstract Dimensions",
      available: 75,
      price: 0.8,
      image: "/placeholder.svg?height=300&width=300",
    },
    {
      id: 4,
      title: "Neon Dreams Series",
      available: 30,
      price: 2,
      image: "/placeholder.svg?height=300&width=300",
    },
    {
      id: 5,
      title: "Quantum Art Piece",
      available: 40,
      price: 1.2,
      image: "/placeholder.svg?height=300&width=300",
    },
    {
      id: 6,
      title: "Future Visions",
      available: 60,
      price: 0.9,
      image: "/placeholder.svg?height=300&width=300",
    },
  ]

  const handlePurchase = (cardId) => {
    setIssuedCards((prev) => new Set([...prev, cardId]))
    // Note: Raffle tickets are now managed by the backend API
  }

  if (!userId) {
    return (
      <div className="min-h-screen bg-neutral-light font-body flex items-center justify-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-neutral-light font-body">
      {/* Google Fonts */}
      <link rel="preconnect" href="https://fonts.googleapis.com" />
      <link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin="anonymous" />
      <link
        href="https://fonts.googleapis.com/css2?family=Montserrat:wght@400;600;700&family=Roboto:wght@300;400;500&display=swap"
        rel="stylesheet"
      />

      <Header />
      <HeroSection />

      {/* Tab Navigation */}
      <div className="container mx-auto px-4 py-8">
        <div className="flex justify-center mb-8">
          <div className="bg-white rounded-lg p-1 shadow-md">
            <button
              onClick={() => setActiveTab('marketplace')}
              className={`px-6 py-3 rounded-md font-semibold transition-all duration-200 ${
                activeTab === 'marketplace'
                  ? 'bg-primary text-white shadow-lg'
                  : 'text-gray-600 hover:text-primary hover:bg-gray-50'
              }`}
            >
              üõçÔ∏è Marketplace
            </button>
            <button
              onClick={() => setActiveTab('collection')}
              className={`px-6 py-3 rounded-md font-semibold transition-all duration-200 ${
                activeTab === 'collection'
                  ? 'bg-primary text-white shadow-lg'
                  : 'text-gray-600 hover:text-primary hover:bg-gray-50'
              }`}
            >
              üì¶ My Collection
            </button>
          </div>
        </div>

        {/* Tab Content */}
        {activeTab === 'marketplace' ? (
          <CollectiblesGrid 
            collectibles={collectibles} 
            onPurchase={handlePurchase} 
            issuedCards={issuedCards}
            userId={userId}
          />
        ) : (
          <PurchasedCards userId={userId} />
        )}
      </div>

      <Footer />

      {/* Persistent Raffle Widget */}
      <div className="fixed bottom-6 right-6 z-40 w-80">
        <RaffleWidget userId={userId} />
      </div>
    </div>
  )
}

export default EvaltreeExclusive
