"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardHeader, CardTitle } from "./ui/"
import { Badge } from "./ui/badge"

const PurchasedCard = ({ card }) => {
  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    })
  }

  const formatPrice = (price, currency = 'eur') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency.toUpperCase(),
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(price / 100) // Convert from cents
  }

  return (
    <div className="bg-white rounded-2xl shadow-lg hover:shadow-xl transition-all duration-300 overflow-hidden transform hover:-translate-y-1">
      {/* Image */}
      <div className="h-64 bg-gradient-to-br from-primary to-accent-gold flex items-center justify-center relative overflow-hidden">
        {card.imageUrl ? (
          <img src={card.imageUrl} alt={card.title} className="w-full h-full object-cover" />
        ) : (
          <div className="text-white text-6xl opacity-50">🎨</div>
        )}

        {/* Purchased Badge */}
        <div className="absolute top-4 right-4">
          <Badge className="bg-green-500 text-white px-3 py-1 rounded-full text-xs font-semibold shadow-lg">
            Purchased
          </Badge>
        </div>
      </div>

      {/* Card content */}
      <div className="p-6">
        <h3 className="text-xl font-heading font-bold text-gray-900 mb-3">{card.title}</h3>

        <div className="flex items-center justify-between mb-4">
          <span className="text-sm font-body text-gray-600">Card ID: {card.id}</span>
          <span className="text-lg font-heading font-bold text-primary">
            {formatPrice(card.price, card.currency)}
          </span>
        </div>

        {/* Purchase Date */}
        <div className="mb-4 p-3 bg-gray-50 rounded-lg">
          <p className="text-sm font-body text-gray-600 mb-2">Purchased on:</p>
          <p className="text-sm font-body text-gray-900 font-semibold">
            {formatDate(card.purchasedAt)}
          </p>
        </div>

        {/* Status section */}
        <div className="border-t pt-4">
          <div className="flex items-center justify-between">
            <span className="text-sm font-body text-gray-500">Collection Status</span>
            <span className="text-xs font-body text-green-600 font-semibold">✅ In Your Collection</span>
          </div>
        </div>
      </div>
    </div>
  )
}

const PurchasedCards = ({ userId }) => {
  const [purchasedCards, setPurchasedCards] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState(null)

  useEffect(() => {
    const fetchPurchasedCards = async () => {
      if (!userId) {
        setLoading(false)
        return
      }

      try {
        const response = await fetch(`/api/purchased-cards?userId=${userId}`)
        const data = await response.json()

        if (data.success) {
          setPurchasedCards(data.cards)
        } else {
          setError(data.error || 'Failed to fetch purchased cards')
        }
      } catch (err) {
        setError('Failed to fetch purchased cards')
        console.error('Error fetching purchased cards:', err)
      } finally {
        setLoading(false)
      }
    }

    fetchPurchasedCards()
  }, [userId])

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading your collection...</p>
        </div>
      </div>
    )
  }

  if (error) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center">
          <p className="text-red-600">Error: {error}</p>
        </div>
      </div>
    )
  }

  if (!userId) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center">
          <p className="text-gray-600">Please log in to view your purchased cards</p>
        </div>
      </div>
    )
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="text-center mb-8">
        <h2 className="text-3xl font-heading font-bold text-gray-900 mb-2">
          Your Collection
        </h2>
        <p className="text-gray-600">
          You have {purchasedCards.length} card{purchasedCards.length !== 1 ? 's' : ''} in your collection
        </p>
      </div>

      {purchasedCards.length === 0 ? (
        <div className="text-center py-12">
          <div className="text-6xl mb-4">📦</div>
          <h3 className="text-xl font-heading font-bold text-gray-900 mb-2">
            No cards yet
          </h3>
          <p className="text-gray-600">
            Purchase your first card to start building your collection!
          </p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {purchasedCards.map((card) => (
            <PurchasedCard key={card.id} card={card} />
          ))}
        </div>
      )}
    </div>
  )
}

export default PurchasedCards
