"use client"

import { useState } from "react"
import { loadStripe } from "@stripe/stripe-js"

const StripeCheckout = ({ onPurchase, isIssued, price = 1, userId }) => {
  const [isLoading, setIsLoading] = useState(false)

  const handleStripeCheckout = async () => {
    setIsLoading(true)

    try {
      // Initialize Stripe (you'll need to replace with your publishable key)
      const stripe = await loadStripe(process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY)

      // Create checkout session
      const response = await fetch('/api/create-checkout-session', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          price: price * 100, // Convert to cents
          userId: userId,
          isIssued: isIssued
        }),
      })

      const session = await response.json()

      if (session.error) {
        console.error('Error creating checkout session:', session.error)
        setIsLoading(false)
        return
      }

      // Redirect to Stripe Checkout
      const result = await stripe.redirectToCheckout({
        sessionId: session.id,
      })

      if (result.error) {
        console.error('Error redirecting to checkout:', result.error)
      }
    } catch (error) {
      console.error('Error during checkout:', error)
    } finally {
      setIsLoading(false)
    }
  }

  return (
    <button
      onClick={handleStripeCheckout}
      disabled={isLoading}
      className={`w-full font-body font-semibold py-3 px-6 rounded-lg transition-all duration-200 hover:shadow-lg transform hover:scale-105 ${
        isLoading ? "bg-gray-400 cursor-not-allowed" : "bg-primary hover:bg-primary/90 text-white"
      }`}
    >
      {isLoading ? (
        <div className="flex items-center justify-center">
          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
          Processing...
        </div>
      ) : (
        `${isIssued ? "Create Now" : "Buy Now"} - €${price}`
      )}
    </button>
  )
}

export default StripeCheckout
