"use client"

import { useState } from "react"

const RaffleTest = ({ userId }) => {
  const [isLoading, setIsLoading] = useState(false)
  const [message, setMessage] = useState("")

  const addTicket = async () => {
    if (!userId) {
      setMessage("No user ID available")
      return
    }

    setIsLoading(true)
    setMessage("")

    try {
      const response = await fetch('http://localhost:3001/api/debug/add-ticket', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ userId }),
      })

      const data = await response.json()

      if (data.success) {
        setMessage(`‚úÖ Added ticket! Total: ${data.ticketCount}`)
        console.log('‚úÖ Manual ticket added:', data)
      } else {
        setMessage(`‚ùå Error: ${data.error}`)
      }
    } catch (error) {
      setMessage(`‚ùå Network error: ${error.message}`)
      console.error('‚ùå Error adding ticket:', error)
    } finally {
      setIsLoading(false)
    }
  }

  const checkRaffleData = async () => {
    setIsLoading(true)
    setMessage("")

    try {
      const response = await fetch('http://localhost:3001/api/debug/raffle-data')
      const data = await response.json()

      setMessage(`üìä Total users: ${data.totalUsers}. Check console for details.`)
      console.log('üîç All raffle data:', data)
    } catch (error) {
      setMessage(`‚ùå Error: ${error.message}`)
    } finally {
      setIsLoading(false)
    }
  }

  if (!userId) {
    return null
  }

  return (
    <div className="fixed top-6 left-6 z-50 bg-white rounded-lg shadow-lg p-4 border">
      <h3 className="text-sm font-bold mb-2">üß™ Raffle Test</h3>
      <p className="text-xs text-gray-600 mb-3">User: {userId.substring(0, 12)}...</p>

      <div className="space-y-2">
        <button
          onClick={addTicket}
          disabled={isLoading}
          className="w-full bg-blue-500 hover:bg-blue-600 text-white text-xs py-1 px-2 rounded disabled:opacity-50"
        >
          {isLoading ? "Adding..." : "Add Ticket"}
        </button>

        <button
          onClick={checkRaffleData}
          disabled={isLoading}
          className="w-full bg-green-500 hover:bg-green-600 text-white text-xs py-1 px-2 rounded disabled:opacity-50"
        >
          {isLoading ? "Checking..." : "Check All Data"}
        </button>
      </div>

      {message && (
        <p className="text-xs mt-2 p-1 bg-gray-100 rounded">
          {message}
        </p>
      )}
    </div>
  )
}

export default RaffleTest
